import { useState } from "react";
import "./works.scss";

export default function Works() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const data = [
    {
      id: "1",
      icon: "./assets/tictactoe.png",
      title: "TicTacToe",
      desc:
        "An interactive game of TicTacToe using JS, HTML5 and CSS",
      img:
      "https://upload.wikimedia.org/wikipedia/de/thumb/f/f9/TicTacToe.svg/1200px-TicTacToe.svg.png",
      link:
      "https://shubhangi013.github.io/TicTacToe/"
    },
    {
      id: "2",
      icon: "./assets/todo.png",
      title: "A To-do webapp",
      desc:
        "A task managemnet app built upon Axios",
      img:
        "https://images-eu.ssl-images-amazon.com/images/I/41da3NERJ4L.png",
      link:
      "https://shubhangi013-todoapp.netlify.app/"
    },
    {
      id: "3",
      icon: "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/800px-React-icon.svg.png",
      title: "To-do React App",
      desc:
        "An interactive task managemnet app built upon Axios and  React .",
      img:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/800px-React-icon.svg.png",
      link :
      "https://csoc-2021-task-3.vercel.app/"
    },
    // {
    //   id: "4",
    //   icon: "./assets/Shubhangi.png",
    //   title: "Portfolio",
    //   desc:
    //     "My personal portfolio website built using React",
    //   img:
    //   "./assets/Shubhangi.png",
    //   link:
    //   ""
    // },
  ];

  const handleClick = (way) => {
    way === "left"
      ? setCurrentSlide(currentSlide > 0 ? currentSlide - 1 : 2)
      : setCurrentSlide(currentSlide < data.length - 1 ? currentSlide + 1 : 0);
  };

  return (
    <div className="works" id="works">
      <div
        className="slider"
        style={{ transform: `translateX(-${currentSlide * 100}vw)` }}
      >
        {data.map((d) => (
          <div className="container">
            <div className="item">
              <div className="left">
                <div className="leftContainer">
                  <div className="imgContainer">
                    <img src={d.icon} alt="" />
                  </div>
                  <h2>{d.title}</h2>
                  <p>{d.desc}</p>

                  <a href={d.link}>View the project!</a>
                </div>
              </div>
              <div className="right">
                <img
                  src={d.img}
                />
              </div>
            </div>
          </div>
        ))}
      </div>
      <img
        src="assets/arrow.png"
        className="arrow left"
        alt=""
        onClick={() => handleClick("left")}
      />
      <img
        src="assets/arrow.png"
        className="arrow right"
        alt=""
        onClick={() => handleClick()}
      />
    </div>
  );
}
